% Simple pseudo random number generator% Marsaglia's theorem is not affecting the modelclassdef LCG < handle	properties (Constant)        m = 2^31;        a = 65539;        c = 0;        seed = 2^31 - 1234567890;    end    properties (Access = private)        lastNumber    end    methods        function obj = LCG            obj.lastNumber = LCG.seed;        end        function number = random(obj)            obj.lastNumber = mod( (LCG.a * obj.lastNumber + LCG.c), LCG.m );            number = obj.lastNumber / LCG.m;        end   endend